<?php
// Set the cookie value to be checked
$cookie = "_|WARNING:-DO-NOT-SHARE-THIS.--Sharing-this-will-allow-someone-to-log-in-as-you-and-to-steal-your-ROBUX-and-items.|_";

// Set the URL to check the cookie against
$url = "https://www.roblox.com/home";

// Initialize cURL
$ch = curl_init();

// Set cURL options
curl_setopt($ch, CURLOPT_URL, $url);
curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
curl_setopt($ch, CURLOPT_COOKIE, ".ROBLOSECURITY=".$cookie);

// Execute cURL request
$response = curl_exec($ch);

// Check if the cookie is valid
if(strpos($response, "My Roblox") !== false) {
    echo "Cookie is valid!";
} else {
    echo "Cookie is invalid.";
}

// Close cURL connection
curl_close($ch);
?>